import React, { useEffect, useState } from 'react';
import { Row, Col } from 'react-bootstrap';
import StatCard from '../components/StatCard';
import AdminLayout from '../components/AdminLayout';
import axios from 'axios';
import "./dashboard-admin.css";

const DashboardAdmin = () => {
  const [stats, setStats] = useState({
    activeScholarships: 0,
    totalRegistrations: 0,
    passedSelections: 0,
    unverifiedApplicants: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        const token = storedUser?.token;
        const response = await axios.get("http://localhost:9900/sms-mgmt/scholarship/get", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = response.data.output_schema.result;

        setStats({
          activeScholarships: data.activeScholarships,
          totalRegistrations: data.totalRegistrations,
          passedSelections: data.passedSelections,
          unverifiedApplicants: data.unverifiedApplicants,
        });
      } catch (error) {
        console.error("Failed to fetch dashboard stats:", error);
      }
    };

    fetchStats();
  }, []);

  return (
    <AdminLayout>
      <h1 className="fs-3 fw-bold mb-4">Dashboard</h1>
      
      <Row>
        <Col xs={12} md={6} lg={3}>
          <StatCard title="Beasiswa Aktif" value={stats.activeScholarships} unit="beasiswa" />
        </Col>
        <Col xs={12} md={6} lg={3}>
          <StatCard title="Jumlah Pendaftaran" value={stats.totalRegistrations} unit="orang" />
        </Col>
        <Col xs={12} md={6} lg={3}>
          <StatCard title="Pendaftar lolos Seleksi" value={stats.passedSelections} unit="orang" />
        </Col>
        <Col xs={12} md={6} lg={3}>
          <StatCard title="Pendaftar Belum diverifikasi" value={stats.unverifiedApplicants} unit="orang" />
        </Col>
      </Row>
    </AdminLayout>
  );
};

export default DashboardAdmin;
