"use client";
import React, { useEffect, useState } from "react";
import axios from "axios";

function DashboardContent() {
  const [fullName, setFullName] = useState("");
  const [scholarshipType, setScholarshipType] = useState("-");
  const [registrationDate, setRegistrationDate] = useState("-");

  useEffect(() => {
    const fetchUserAndScholarship = async () => {
      const storedUser = JSON.parse(localStorage.getItem("user"));

      if (!storedUser) return;

      const firstName = storedUser?.user?.first_name || "-";
      const lastName = storedUser?.user?.last_name || "-";
      setFullName(`${lastName}, ${firstName}`);

      const userUuid = storedUser?.user?.uuid;
      const token = storedUser?.token;

      if (!userUuid || !token) {
        console.error("UUID atau token tidak ditemukan.");
        return;
      }

      try {
        const response = await axios.get(
          `http://localhost:9900/sms-mgmt/scholarship/get?userUuid=${userUuid}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        const scholarshipRecords = response.data.output_schema.records;

        if (scholarshipRecords && scholarshipRecords.length > 0) {
          setScholarshipType(scholarshipRecords[0].scholarshipType || "-");

          const createdAt = scholarshipRecords[0].createdAt;
          if (createdAt) {
            const dateObj = new Date(createdAt);
            const formattedDate = dateObj.toLocaleDateString("id-ID", {
              day: "2-digit",
              month: "long",
              year: "numeric",
            });
            setRegistrationDate(formattedDate);
          }
        } else {
          setScholarshipType("-");
          setRegistrationDate("-");
        }
      } catch (error) {
        console.error("Error fetching scholarship:", error);
        setScholarshipType("-");
        setRegistrationDate("-");
      }
    };

    fetchUserAndScholarship();
  }, []);

  return (
    <>
      <link
        href="https://fonts.googleapis.com/css2?family=Onest:wght@100..900&display=swap"
        rel="stylesheet"
      />
      <main className="col-lg-8" style={{ fontFamily: "'Onest', sans-serif" }}>
        <div className="py-5">
          <h2 className="fw-bold">Hai, Selamat Datang</h2>
          <p className="h4 fw-normal mt-3">{fullName}</p>

          {/* Card Informasi Beasiswa */}
          <section className="mt-5">
            <h3 className="fw-bold mb-3">Informasi Beasiswa</h3>

            <div className="card shadow-sm rounded-3 border-0 p-4" style={{ backgroundColor: "#ffffff" }}>
              <h5 className="mb-2">Jenis Beasiswa</h5>
              <p className="h5 fw-semibold mb-4">{scholarshipType}</p>

              <h5 className="mb-2">Tanggal Daftar</h5>
              <p className="h5 fw-semibold">{registrationDate}</p>
            </div>
          </section>

          <section className="mt-5">
            <h3 className="fw-bold">Pengumuman</h3>
          </section>
        </div>
      </main>
    </>
  );
}

export default DashboardContent;
