"use client";

import React, { useEffect, useState } from "react";
import axios from "axios";

export default function DocumentValidation() {
  const [documents, setDocuments] = useState([]);
  const [isComplete, setIsComplete] = useState(false);

  useEffect(() => {
    const fetchDocuments = async () => {
      try {
        const uploadedBy = "user123"; // Ganti sesuai user login
        const response = await axios.get(`http://localhost:9900/smsdocument/check-completeness?uploadedBy=${uploadedBy}`);
        const data = response.data;

        // Assume kalau data.missingCategories itu array yang belum ada
        const requiredDocuments = [
          "Form Biodata A1",
          "Form Keterampilan",
          "Surat Keterangan Tidak Mampu",
          "Resume Pribadi",
          "Motivation Letter",
          "Surat Pernyataan sedan tidak menerima beasiswa lain",
          "Surat pernyataan bermetrai",
          "Fotocopy KTP",
          "Fotocopy KTM (ID Card)",
          "IPK Semester terakhir",
          "Kartu hasil studi (Transkrip Nilai)"
        ];

        const docList = requiredDocuments.map(doc => ({
          name: doc,
          isUploaded: !data.missingCategories.includes(doc)
        }));

        setDocuments(docList);
        setIsComplete(data.isComplete);
      } catch (error) {
        console.error("Gagal fetch data:", error);
      }
    };

    fetchDocuments();
  }, []);

  return (
    <section className="mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="fs-4 fw-bold m-0">Cek Dokumen</h2>
        <div className="d-flex gap-2">
          <span>Status</span>
          <span>:</span>
          <span className={isComplete ? "text-success" : "text-danger"}>
            {isComplete ? "Lengkap" : "Tidak Lengkap"}
          </span>
        </div>
      </div>

      <div className="list-group border-0">
        {documents.map((doc, index) => (
          <div
            key={index}
            className="list-group-item border-0 bg-transparent d-flex justify-content-between align-items-center"
          >
            <div className="d-flex gap-3 align-items-center">
              <input
                type="checkbox"
                className="form-check-input"
                checked={doc.isUploaded}
                readOnly
              />
              <span>{doc.name}</span>
            </div>
            <div className="d-flex gap-3">
              <span>:</span>
              <span className={doc.isUploaded ? "text-success" : "text-muted"}>
                {doc.isUploaded ? "Tersedia" : "Tidak ada File"}
              </span>
            </div>
          </div>
        ))}
      </div>

      <button className="btn btn-success rounded-pill px-5 mt-5">
        Selesai
      </button>
    </section>
  );
}
