"use client";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import apiClient from "../../../../../api/apiClient";

const documentList = [
  "Pass Foto",
  "Form Biodata A1",
  "Form Keterampilan",
  "Surat Keterangan Tidak Mampu",
  "Resume Pribadi",
  "Motivation Letter",
  "Surat Pernyataan Tidak Menerima Beasiswa Lain",
  "Surat Pernyataan Bermetrai",
  "Fotocopy KTP",
  "Fotocopy KTM",
  "Transkrip Nilai",
];

export default function DocumentValidation() {
  const { uploadedBy } = useParams();
  const [documents, setDocuments] = useState({});
  const [loading, setLoading] = useState(true);
  const [allComplete, setAllComplete] = useState(false);
  const [verifications, setVerifications] = useState({});
  const [notes, setNotes] = useState({});
  const [errorMessage, setErrorMessage] = useState(null);

  useEffect(() => {
    const fetchDocuments = async () => {
      try {
        if (!uploadedBy) {
          console.error("Parameter uploadedBy tidak ditemukan di URL.");
          return;
        }

        const response = await apiClient.get(`/document/get?uploadedBy=${uploadedBy}`);
        const uploadedDocuments = response.data?.output_schema?.records || [];

        const newDocuments = {};
        const newVerifications = {};
        const newNotes = {};

        documentList.forEach((docName) => {
          const matchedDoc = uploadedDocuments.find((doc) => doc.category === docName);
          if (matchedDoc) {
            newDocuments[docName] = matchedDoc;
            newVerifications[docName] = matchedDoc.is_verified ?? null;
            newNotes[docName] = matchedDoc.note || "";
          } else {
            newDocuments[docName] = null;
            newVerifications[docName] = null;
            newNotes[docName] = "";
          }
        });

        setDocuments(newDocuments);
        setVerifications(newVerifications);
        setNotes(newNotes);

        const isAllComplete = Object.values(newDocuments).every(Boolean);
        setAllComplete(isAllComplete);
      } catch (error) {
        setErrorMessage("Gagal mengambil dokumen, silakan coba lagi.");
        console.error("Gagal mengambil dokumen:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDocuments();
  }, [uploadedBy]);

  const handleVerificationChange = (category, value) => {
    setVerifications((prev) => ({ ...prev, [category]: value }));
  };

  const handleNoteChange = (category, value) => {
    setNotes((prev) => ({ ...prev, [category]: value }));
  };

  const handleVerifySubmit = async (category) => {
    try {
      const document = documents[category];
      const isVerified = verifications[category];
      const note = notes[category] ?? "";

      if (isVerified === null) {
        alert("Silakan centang terlebih dahulu untuk memverifikasi atau menolak.");
        return;
      }

      if (isVerified === false) {
        const confirmReject = window.confirm(`Apakah Anda yakin ingin MENOLAK dokumen "${category}"?`);
        if (!confirmReject) return;
      }

      await apiClient.patch(
        `/document/verify?uuid=${document.uuid}&isVerified=${isVerified}&note=${encodeURIComponent(note)}`
      );

      alert(`Dokumen "${category}" telah diverifikasi.`);
    } catch (error) {
      alert(`Gagal memverifikasi dokumen "${category}".`);
      console.error(error);
    }
  };

  const handleDocumentCompletionStatus = async () => {
    try {
      const isAllVerified = Object.values(verifications).every((verified) => verified === true);

      await apiClient.patch(
        `/document/completion-status?uuid=${uploadedBy}&isComplete=${isAllVerified}`
      );

      alert(isAllVerified ? "Dokumen lengkap." : "Dokumen tidak lengkap.");
    } catch (error) {
      alert("Gagal mengupdate status kelengkapan dokumen.");
      console.error(error);
    }
  };

  if (loading) {
    return <div className="text-center py-5">Loading cek dokumen...</div>;
  }

  return (
    <section className="mt-5">
      {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="fs-4 fw-bold m-0">Cek Dokumen</h2>
        <div className="d-flex gap-2">
          <span>Status</span>
          <span>:</span>
          <span className={allComplete ? "text-success" : "text-danger"}>
            {allComplete ? "Lengkap" : "Tidak Lengkap"}
          </span>
        </div>
      </div>

      <div className="list-group border-0">
        {documentList.map((doc, idx) => (
          <div key={idx} className="list-group-item border-0 bg-transparent mb-3">
            <div className="d-flex align-items-center gap-3 mb-2">
              <span className="fw-medium">{doc}</span>
              <span className="mx-2">:</span>
              {documents[doc] ? (
                <a
                  href={`http://localhost:9900/sms-mgmt/document/preview/${documents[doc]?.uuid}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-decoration-none text-success"
                >
                  Lihat
                </a>
              ) : (
                <span className="text-muted">Tidak ada File</span>
              )}
            </div>

            {documents[doc] && (
              <div className="ps-4">
                <div className="mb-2">
                  <label className="form-label mb-1">Catatan:</label>
                  <textarea
                    rows="2"
                    className="form-control"
                    value={notes[doc] || ""}
                    onChange={(e) => handleNoteChange(doc, e.target.value)}
                  />
                </div>
                <div className="mb-3 d-flex flex-column gap-2">
                  <div className="d-flex align-items-center gap-3">
                    <input
                      type="checkbox"
                      checked={verifications[doc] === true}
                      onChange={(e) => handleVerificationChange(doc, e.target.checked)}
                    />
                    <button
                      className="btn btn-sm btn-primary"
                      onClick={() => handleVerifySubmit(doc)}
                      disabled={verifications[doc] === null}
                    >
                      Simpan Verifikasi
                    </button>
                  </div>

                  <div>
                    <small className={`fw-semibold ${verifications[doc] ? "text-success" : "text-danger"}`}>
                      {verifications[doc] === null
                        ? "Belum diperiksa"
                        : verifications[doc]
                        ? "✅ Dokumen valid"
                        : "❌ Dokumen tidak valid"}
                    </small>
                    <br />
                    {notes[doc] && <small className="text-muted">Catatan: {notes[doc]}</small>}
                  </div>
                </div>

              </div>
            )}
          </div>
        ))}
      </div>

      <button
        className="btn btn-success rounded-pill px-5 mt-5"
        disabled={!allComplete}
        onClick={handleDocumentCompletionStatus}
      >
        {allComplete ? "Selesai" : "Lengkapi Dulu"}
      </button>
    </section>
  );
}
