"use client";
import React, { useCallback, useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import apiClient from "../../../../../api/apiClient";

export const StudentDetailsAndParentInformation = () => {
  const { uuid } = useParams();
  const [student, setStudent] = useState(null);
  const [photoUrl, setPhotoUrl] = useState(null);
  const [loading, setLoading] = useState(true);
  const [approvalStatus, setApprovalStatus] = useState("IN_PROGRESS");
  const [documentStatus, setDocumentStatus] = useState("Belum Lengkap");
  const [autoRejectedReason, setAutoRejectedReason] = useState("");
  const [autoRejected, setAutoRejected] = useState(false);
  const [canApprove, setCanApprove] = useState(false);

  const fetchStudentDetails = useCallback(async () => {
    try {
      const storedUser = JSON.parse(localStorage.getItem("user"));
      const token = storedUser?.token;
      if (!token || !uuid) return;

      const response = await apiClient.get(`/scholarship/detail?userUuid=${uuid}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const studentData = response.data.output_schema.result;
      setStudent(studentData);

      const docResponse = await apiClient.get(`/document/get?uploadedBy=${uuid}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const records = docResponse.data?.output_schema?.records || [];
      const passFoto = records.find(doc => doc.category === "Pass Foto");
      if (passFoto) {
        setPhotoUrl(`http://localhost:9900/sms-mgmt/document/preview?uuid=${passFoto.uuid}`);
      }

      const documentCompletionStatus = studentData.document_completion_status;
      setDocumentStatus(documentCompletionStatus ? "Lengkap" : "Belum Lengkap");

      const scholarUuid = studentData.uuid;

      if (
        documentCompletionStatus &&
        !["INTERVIEW", "APPROVED", "REJECTED"].includes(studentData.scholarship_status)
      ) {
        await apiClient.put(`/scholarship/interview?uuid=${scholarUuid}&isValid=true`, null, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setApprovalStatus("INTERVIEW");
        setCanApprove(true);
        setAutoRejected(false);
      } else {
        setApprovalStatus(studentData.scholarship_status || "IN_PROGRESS");
        setCanApprove(studentData.scholarship_status === "INTERVIEW");
        setAutoRejected(!documentCompletionStatus);
        if (!documentCompletionStatus) {
          setAutoRejectedReason("Menunggu validasi data atau kelengkapan dokumen.");
        }
      }
    } catch (error) {
      console.error("Error fetching student detail:", error);
    } finally {
      setLoading(false);
    }
  }, [uuid]);

  const handleApprovalChange = async () => {
    if (autoRejected || !canApprove) {
      alert("Tidak dapat menyetujui karena data/dokumen belum lengkap atau valid.");
      return;
    }

    try {
      const storedUser = JSON.parse(localStorage.getItem("user"));
      const token = storedUser?.token;
      const scholarUuid = student?.uuid;

      await apiClient.put(`/scholarship/approval?uuid=${scholarUuid}&isApproved=true`, null, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("Status pendaftaran berhasil diperbarui.");
      fetchStudentDetails();
    } catch (error) {
      console.error("Error changing approval status:", error);
      alert("Gagal mengubah status pendaftaran.");
    }
  };

  useEffect(() => {
    fetchStudentDetails();
  }, [fetchStudentDetails]);

  if (loading) return <div className="text-center py-5">Loading detail mahasiswa...</div>;
  if (!student) return <div className="text-center py-5 text-danger">Data mahasiswa tidak ditemukan.</div>;

  return (
    <div className="container py-4">
      <div className="mb-4">
        <Link to="/bidang/informasi-pendaftar">&larr; Kembali ke daftar mahasiswa</Link>
        <h3 className="mt-2">Detail Pendaftar</h3>
      </div>
      <div className="row">
        <div className="col-md-6">
          <div className="text-center mb-4">
            {photoUrl ? (
              <img src={photoUrl} className="rounded-circle" alt="Foto Mahasiswa" width="150" height="150" />
            ) : (
              <div className="rounded-circle bg-secondary" style={{ width: 150, height: 150 }} />
            )}
            <h4 className="mt-3">{student.master_user?.last_name}, {student.master_user?.first_name}</h4>
            <p>{student.nim} / {student.no_registration}</p>
          </div>

          <table className="table table-bordered">
            <tbody>
              <tr><td>Fakultas</td><td>{student.major?.faculty_name}</td></tr>
              <tr><td>Jurusan</td><td>{student.major?.major_name}</td></tr>
              <tr><td>Email</td><td>{student.master_user?.email}</td></tr>
              <tr><td>No. Telepon</td><td>{student.master_user?.phone_number}</td></tr>
              <tr><td>IPK</td><td>{student.master_user?.gpa}</td></tr>
              <tr><td>Beasiswa</td><td>{student.scholarship_type}</td></tr>
              <tr><td>Status Dokumen</td><td>{documentStatus}</td></tr>
            </tbody>
          </table>

          <div className="mt-4">
            <select className="form-select mb-3" value={approvalStatus} onChange={(e) => setApprovalStatus(e.target.value)}>
              <option value="">Pilih Status Approval</option>
              <option value="APPROVED" disabled={!canApprove}>Setujui Pendaftaran</option>
              <option value="REJECTED">Tolak Pendaftaran</option>
            </select>
            <button className="btn btn-primary" onClick={handleApprovalChange} disabled={!approvalStatus}>Simpan Status</button>
          </div>
          {autoRejectedReason && (
            <div className="alert alert-warning mt-3">{autoRejectedReason}</div>
          )}
        </div>
        <div className="col-md-6">
          <h5>Informasi Orang Tua</h5>
          <h6 className="mt-3">Ayah</h6>
          <ul>
            <li>Nama: {student.father_name}</li>
            <li>Alamat: {student.father_address}</li>
            <li>Pekerjaan: {student.father_job}</li>
            <li>No. Telepon: {student.father_phone}</li>
          </ul>
