import React, { useEffect, useState } from 'react';
import { Row, Col } from 'react-bootstrap';
import StatCard from '../components/StatCard';
import AdminLayout from '../components/AdminLayout';
import axios from 'axios';
import "./dashboard-admin.css";

const DashboardAdmin = () => {
  const [summary, setSummary] = useState({
    total: 0,
    inProgress: 0,
    approved: 0,
    rejected: 0,
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSummary = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        const token = storedUser?.token;

        if (!token) {
          console.error("No token found.");
          return;
        }

        const response = await axios.get("http://localhost:9900/sms-mgmt/scholarship/get", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const records = response.data.output_schema.records || [];

        const total = records.length;
        const inProgress = records.filter((s) => s.status === "IN_PROGRESS").length;
        const approved = records.filter((s) => s.status === "APPROVED").length;
        const rejected = records.filter((s) => s.status === "REJECTED").length;

        setSummary({ total, inProgress, approved, rejected });

      } catch (error) {
        console.error("Error fetching scholarship summary:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchSummary();
  }, []);

  const getPercentage = (count) => {
    if (summary.total === 0) return 0;
    return ((count / summary.total) * 100).toFixed(0);
  };

  return (
    <AdminLayout>
      <h1 className="fs-3 fw-bold mb-4">Dashboard</h1>
      
      <Row>
        <Col xs={12} md={6} lg={3}>
          <StatCard title="Beasiswa Aktif" value={stats.activeScholarships} unit="beasiswa" />
        </Col>
        <Col xs={12} md={6} lg={3}>
          <StatCard title="Jumlah Pendaftaran" value={stats.totalRegistrations} unit="orang" />
        </Col>
        <Col xs={12} md={6} lg={3}>
          <StatCard title="Pendaftar lolos Seleksi" value={stats.passedSelections} unit="orang" />
        </Col>
        <Col xs={12} md={6} lg={3}>
          <StatCard title="Pendaftar Belum diverifikasi" value={stats.unverifiedApplicants} unit="orang" />
        </Col>
      </Row>
    </AdminLayout>
  );
};

export default DashboardAdmin;
