"use client";
import React, { useEffect, useState } from "react";
import { FilterBar } from "./FilterBar";
import { StudentCard } from "./StudentCard";
import axios from "axios";

export const StudentList = () => {
  const [students, setStudents] = useState([]);

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        const token = storedUser?.token;

        if (!token) {
          console.error("No token found.");
          return;
        }

        const response = await axios.get("http://localhost:9900/sms-mgmt/scholarship/get", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        const fetchedStudents = response.data.output_schema.records || [];

        // Map supaya sesuai dengan props
        const formattedStudents = fetchedStudents.map((student) => ({
          uuid: student.uuid,
          faculty: student.major?.faculty?.name || "-",
          name: `${student.masterUser?.lastName}, ${student.masterUser?.firstName}`,
          id: `${student.masterUser?.nim} / ${student.masterUser?.userName}`,
        }));

        setStudents(formattedStudents);
      } catch (error) {
        console.error("Error fetching students:", error);
      }
    };

    fetchStudents();
  }, []);

  return (
    <main className="mt-4">
      <FilterBar />
      <div className="mt-4">
        {students.length > 0 ? (
          students.map((student, index) => (
            <StudentCard
              key={index}
              faculty={student.faculty}
              name={student.name}
              id={student.id}
              uuid={student.uuid}
            />
          ))
        ) : (
          <div className="text-center text-secondary">Belum ada pendaftar</div>
        )}
      </div>
    </main>
  );
};
