import React from "react";

export const FormInput = ({
  label,
  name,
  placeholder,
  value,
  onChange,
  isSelect = false,
  options = [],
}) => {
  return (
    <div className="mb-4">
      {label && (
        <label htmlFor={name} className="form-label fw-semibold">
          {label}
        </label>
      )}

      {isSelect ? (
        <select
          id={name}
          name={name}
          className="form-select"
          value={value}
          onChange={onChange}
        >
          <option value="">-- Pilih {label || name} --</option>
          {options.map((option) => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
      ) : (
        <input
          id={name}
          type="text"
          className="form-control"
          name={name}
          placeholder={placeholder}
          value={value}
          onChange={onChange}
        />
      )}
    </div>
  );
};
