"use client";
import React, { useEffect, useState } from "react";
import axios from "axios";

function TableSection() {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        const token = storedUser?.token;

        const response = await axios.get("http://localhost:9900/sms-mgmt/scholarship/list", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const fetchedData = response.data.output_schema.records || [];
        setStudents(fetchedData);

      } catch (error) {
        console.error("Failed to fetch student data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchStudents();
  }, []);

  if (loading) {
    return <div className="text-center py-5">Loading daftar mahasiswa...</div>;
  }

  return (
    <section className="mb-0 mt-0">
      <div className="table-responsive">
        <table className="table table-sm">
          <thead className="bg-light">
            <tr className="small">
              <th className="py-2"></th>
              <th className="py-2">#</th>
              <th className="py-2">Nama Lengkap</th>
              <th className="py-2">NIM</th>
              <th className="py-2">Fakultas</th>
              <th className="py-2">Jurusan</th>
              <th className="py-2">Beasiswa</th>
              <th className="py-2">Tanggal</th>
            </tr>
          </thead>
          <tbody className="small">
            {students.map((student, index) => (
              <tr key={student.uuid}>
                <td className="py-1">
                  <img
                    src={student.iconUrl || "https://via.placeholder.com/12"} 
                    alt=""
                    style={{ width: "12px" }}
                  />
                </td>
                <td className="py-1">{index + 1}</td>
                <td className="py-1">
                  {student.master_user?.last_name}, {student.master_user?.first_name}
                </td>
                <td className="py-1">{student.nim}</td>
                <td className="py-1">{student.major?.faculty_name}</td>
                <td className="py-1">{student.major?.major_name}</td>
                <td className="py-1">{student.scholarship_type}</td>
                <td className="py-1">
                  {new Date(student.createdAt).toLocaleDateString('id-ID', {
                    day: '2-digit', month: 'long', year: 'numeric'
                  })}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </section>
  );
}

export default TableSection;
