"use client";
import React, { useEffect, useState } from "react";
import axios from "axios";

const requiredDocuments = [
  "Form Biodata A1",
  "Form Keterampilan",
  "Surat Keterangan Tidak Mampu",
  "Resume Pribadi",
  "Motivation Letter",
  "Surat Pernyataan Tidak Menerima Beasiswa Lain",
  "Surat Pernyataan Bermetrai",
  "Fotocopy KTP",
  "Fotocopy KTM",
  "IPK Semester terakhir",
  "Transkrip Nilai"
];

export default function DocumentValidation() {
  const [uploadedDocs, setUploadedDocs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDocuments = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        const token = storedUser?.token;
        const userUuid = storedUser?.user?.uuid || storedUser?.uuid;

        if (!token || !userUuid) {
          console.error("Missing token or user UUID.");
          return;
        }

        const response = await axios.get(`http://localhost:9900/sms-mgmt/document/?uploadedBy=${userUuid}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const documents = response.data.output_schema.records || [];
        setUploadedDocs(documents);
      } catch (error) {
        console.error("Error fetching documents:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDocuments();
  }, []);

  const checkDocument = (category) => {
    return uploadedDocs.find((doc) => doc.category === category);
  };

  const isComplete = requiredDocuments.every((doc) => checkDocument(doc));

  return (
    <section className="mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="fs-4 fw-bold m-0">Cek Dokumen</h2>
        <div className="d-flex gap-2">
          <span>Status</span>
          <span>:</span>
          <span className={isComplete ? "text-success" : "text-danger"}>
            {isComplete ? "Lengkap" : "Tidak Lengkap"}
          </span>
        </div>
      </div>

      {loading ? (
        <div>Loading dokumen...</div>
      ) : (
        <div className="list-group border-0">
          {requiredDocuments.map((docName, index) => {
            const doc = checkDocument(docName);
            return (
              <div key={index} className="list-group-item border-0 bg-transparent d-flex justify-content-between align-items-center">
                <div className="d-flex gap-3 align-items-center">
                  <input type="checkbox" className="form-check-input" checked={!!doc} readOnly />
                  <span>{docName}</span>
                </div>
                <div className="d-flex gap-3">
                  <span>:</span>
                  <span className={doc ? "text-success" : "text-muted"}>
                    {doc ? doc.fileName : "Tidak ada File"}
                  </span>
                </div>
              </div>
            );
          })}
        </div>
      )}

      <button className="btn btn-success rounded-pill px-5 mt-5">
        Selesai
      </button>
    </section>
  );
}
