import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import apiClient from "../../../../api/apiClient";

const AnnouncementDetail = () => {
  const { state } = useLocation(); // Retrieve the state passed from the previous page
  const [announcement, setAnnouncement] = useState(state || {});
  const navigate = useNavigate();

  useEffect(() => {
    if (!state) {
      // Fetch the announcement details if no state was passed
      const fetchAnnouncementDetail = async () => {
        try {
          const response = await apiClient.get(
            `/announcement/detail?uuid=${state?.uuid}`
          );
          setAnnouncement(response.data);
        } catch (error) {
          console.error("Error fetching announcement details:", error);
        }
      };
      fetchAnnouncementDetail();
    }
  }, [state]);

  return (
    <section className="py-4">
      <h2 className="h4 fw-bold mb-4">Detail Pengumuman</h2>
      <div>
        <h5>{announcement.title}</h5>
        <p>{announcement.category}</p>
        <p>{new Date(announcement.createdAt).toLocaleDateString()}</p>
        <p>{announcement.description}</p>
      </div>
      <button
        className="btn btn-primary"
        onClick={() => navigate(`/admin/pengumuman/update/${announcement.uuid}`, { state: announcement })}
      >
        Edit Pengumuman
      </button>
    </section>
  );
};

export default AnnouncementDetail;
