import React, { useState } from 'react';
import { Form, Button, Container, Row, Col, Toast, ToastContainer } from 'react-bootstrap';
import AdminLayout from '../../../Pages/Admin/components/AdminLayout';
import apiClient from '../../../../api/apiClient';

const AddBeasiswa = () => {
  const [formData, setFormData] = useState({
    nama: '',
    deskripsi: '',
    ipk: '',
    semester: '',
    kuota: ''
  });

  const [toast, setToast] = useState({
    show: false,
    message: '',
    variant: 'success'
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem("token");
    if (!token) {
      setToast({ show: true, message: 'Silakan login terlebih dahulu.', variant: 'danger' });
      return;
    }

    const ipkFloat = parseFloat(formData.ipk.replace(',', '.'));
    const semesterInt = parseInt(formData.semester);
    const kuotaInt = parseInt(formData.kuota);

    if (isNaN(ipkFloat) || isNaN(semesterInt) || isNaN(kuotaInt)) {
      setToast({ show: true, message: 'IPK, Semester, dan Kuota harus berupa angka yang valid.', variant: 'danger' });
      return;
    }

    try {
      const response = await apiClient.post("/scholarshipType/create", {
        uuid: null, // jika UUID di-generate oleh backend
        scholarship_name: formData.nama,
        description: formData.deskripsi,
        applicant_quota: kuotaInt,
        minimum_gpa: ipkFloat,
        minimum_semester: semesterInt,
        isActive: true
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });

      console.log("Respon backend:", response.data);

      setToast({ show: true, message: 'Beasiswa berhasil ditambahkan!', variant: 'success' });

      setFormData({
        nama: '',
        deskripsi: '',
        ipk: '',
        semester: '',
        kuota: ''
      });
    } catch (error) {
      console.error("Error saat submit:", error.response || error.message || error);
      setToast({
        show: true,
        message: error?.response?.data?.error_schema?.error_message?.indonesian || 'Terjadi kesalahan saat menambahkan beasiswa.',
        variant: 'danger'
      });
    }
  };

  return (
    <AdminLayout>
      <Container className="mt-5">
        <h5><a href="/admin/manajemen-beasiswa">Daftar Beasiswa</a> / <span className="text-primary">Tambah Beasiswa</span></h5>
        <Form onSubmit={handleSubmit} className="bg-light p-4 rounded mt-3">
          <Form.Group className="mb-3">
            <Form.Label>Nama Beasiswa</Form.Label>
            <Form.Control type="text" name="nama" value={formData.nama} onChange={handleChange} placeholder="Nama Beasiswa" />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Deskripsi</Form.Label>
            <Form.Control as="textarea" rows={3} name="deskripsi" value={formData.deskripsi} onChange={handleChange} placeholder="Deskripsi" />
          </Form.Group>

          <Row>
            <Col md={4}>
              <Form.Group className="mb-3">
                <Form.Label>IPK Minimal</Form.Label>
                <Form.Control type="text" name="ipk" value={formData.ipk} onChange={handleChange} placeholder="Contoh: 3.25" />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group className="mb-3">
                <Form.Label>Semester Minimal</Form.Label>
                <Form.Select name="semester" value={formData.semester} onChange={handleChange}>
                  <option value="">Pilih Semester</option>
                  {[...Array(8)].map((_, i) => (
                    <option key={i + 1} value={i + 1}>{i + 1}</option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group className="mb-3">
                <Form.Label>Kuota Pendaftar</Form.Label>
                <Form.Control type="number" name="kuota" value={formData.kuota} onChange={handleChange} placeholder="Jumlah Pendaftar" />
              </Form.Group>
            </Col>
          </Row>

          <Button variant="success" type="submit">Simpan</Button>
        </Form>

        <ToastContainer position="top-end" className="p-3">
          <Toast bg={toast.variant} show={toast.show} onClose={() => setToast({ ...toast, show: false })} delay={3000} autohide>
            <Toast.Body className="text-white">{toast.message}</Toast.Body>
          </Toast>
        </ToastContainer>
      </Container>
    </AdminLayout>
  );
};

export default AddBeasiswa;
