import React, { useEffect, useState } from 'react';
import { Button, Modal, Form } from 'react-bootstrap';
import AdminLayout from '../components/AdminLayout';
import BeasiswaItem from '../components/BeasiswaItem';
import { useNavigate } from 'react-router-dom';

// Gunakan require untuk mengubah logo lokal ke string path
const logoGenbiPath = require('../');
const kipLogoPath = require('../../../img/kip.png');

const ManajemenBeasiswa = () => {
  const navigate = useNavigate();

  const [beasiswaList, setBeasiswaList] = useState(() => {
    const stored = localStorage.getItem("beasiswaList");
    return stored
      ? JSON.parse(stored)
      : [
          { id: 1, nama: 'GenBI', logo: logoGenbiPath },
          { id: 2, nama: 'KIP Kuliah', logo: kipLogoPath }
        ];
  });

  useEffect(() => {
    localStorage.setItem("beasiswaList", JSON.stringify(beasiswaList));
  }, [beasiswaList]);

  const [showModal, setShowModal] = useState(false);
  const [newBeasiswa, setNewBeasiswa] = useState({ nama: '', logo: '' });

  const handleAddBeasiswa = () => {
    const newEntry = {
      id: Date.now(),
      nama: newBeasiswa.nama,
      logo: newBeasiswa.logo || 'https://via.placeholder.com/50'
    };

    setBeasiswaList([...beasiswaList, newEntry]);
    setShowModal(false);
    setNewBeasiswa({ nama: '', logo: '' });
  };

  const handleUpdate = (id) => {
    alert(`Update beasiswa dengan ID: ${id}`);
  };

  const handleDelete = (id) => {
    if (window.confirm('Apakah yakin ingin menghapus beasiswa ini?')) {
      const updatedList = beasiswaList.filter(b => b.id !== id);
      setBeasiswaList(updatedList);
    }
  };

  const handleDetail = (id) => {
    navigate(`/admin/manajemen-beasiswa/detail/${id}`);
  };

  return (
    <AdminLayout>
      <div className="container mt-4">
        <h3>Daftar Beasiswa</h3>
        <Button className="mb-3" onClick={() => setShowModal(true)}>Tambahkan Beasiswa +</Button>

        {beasiswaList.map((beasiswa) => (
          <BeasiswaItem
            key={beasiswa.id}
            beasiswa={beasiswa}
            onUpdate={() => handleUpdate(beasiswa.id)}
            onDelete={() => handleDelete(beasiswa.id)}
            onDetail={() => handleDetail(beasiswa.id)}
          />
        ))}

        <Modal show={showModal} onHide={() => setShowModal(false)}>
          <Modal.Header closeButton>
            <Modal.Title>Tambah Beasiswa</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Nama Beasiswa</Form.Label>
                <Form.Control
                  type="text"
                  value={newBeasiswa.nama}
                  onChange={(e) => setNewBeasiswa({ ...newBeasiswa, nama: e.target.value })}
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>URL Logo (atau nama file dari /img)</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="contoh: /img/genbi.png"
                  value={newBeasiswa.logo}
                  onChange={(e) => setNewBeasiswa({ ...newBeasiswa, logo: e.target.value })}
                />
              </Form.Group>
              {newBeasiswa.logo && (
                <div className="text-center mt-3">
                  <img
                    src={newBeasiswa.logo}
                    alt="Preview Logo"
                    style={{ width: "100px", height: "100px", objectFit: "contain" }}
                  />
                </div>
              )}
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowModal(false)}>Batal</Button>
            <Button variant="primary" onClick={handleAddBeasiswa}>Tambah</Button>
          </Modal.Footer>
        </Modal>
      </div>
    </AdminLayout>
  );
};

export default ManajemenBeasiswa;
