"use client";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import apiClient from "../../../../../api/apiClient";

// Daftar kategori dokumen wajib
const documentList = [
  "Pass Foto",
  "Form Biodata A1",
  "Form Keterampilan",
  "Surat Keterangan Tidak Mampu",
  "Resume Pribadi",
  "Motivation Letter",
  "Surat Pernyataan Tidak Menerima Beasiswa Lain",
  "Surat Pernyataan Bermetrai",
  "Fotocopy KTP",
  "Fotocopy KTM",
  "Transkrip Nilai",
];

export default function DocumentValidation() {
  const { uploadedBy } = useParams(); // Ambil UUID beasiswa dari URL
  const [documents, setDocuments] = useState({});
  const [loading, setLoading] = useState(true);
  const [allComplete, setAllComplete] = useState(false);

  useEffect(() => {
    const fetchDocuments = async () => {
      try {
        if (!uploadedBy) {
          console.error("Parameter uploadedBy tidak ditemukan di URL.");
          return;
        }

        // Ambil dokumen berdasarkan UUID scholarship
        const response = await apiClient.get(`/document/get?uploadedBy=${uploadedBy}`);
        const uploadedDocuments = response.data?.output_schema?.records || [];

        // Tandai dokumen yang tersedia
        const newDocuments = {};
        documentList.forEach((docName) => {
          const isUploaded = uploadedDocuments.some((doc) => doc.category === docName);
          newDocuments[docName] = isUploaded;
        });

        setDocuments(newDocuments);

        // Cek kelengkapan dokumen
        const isAllComplete = Object.values(newDocuments).every(Boolean);
        setAllComplete(isAllComplete);
      } catch (error) {
        console.error("Gagal mengambil dokumen:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDocuments();
  }, [uploadedBy]);

  if (loading) {
    return <div className="text-center py-5">Loading cek dokumen...</div>;
  }

  return (
    <section className="mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="fs-4 fw-bold m-0">Cek Dokumen</h2>
        <div className="d-flex gap-2">
          <span>Status</span>
          <span>:</span>
          <span className={allComplete ? "text-success" : "text-danger"}>
            {allComplete ? "Lengkap" : "Tidak Lengkap"}
          </span>
        </div>
      </div>

      <div className="list-group border-0">
        {documentList.map((doc, idx) => (
          <div
            key={idx}
            className="list-group-item border-0 bg-transparent d-flex justify-content-between align-items-center"
          >
            <div className="d-flex gap-3 align-items-center">
              <input
                type="checkbox"
                className="form-check-input"
                checked={documents[doc]}
                readOnly
              />
              <span>{doc}</span>
            </div>
            <div className="d-flex gap-3">
              <span>:</span>
              <span className={documents[doc] ? "text-success" : "text-muted"}>
                {documents[doc] ? (
                  <a
                    href={`http://localhost:9900/sms-mgmt/document/preview/${documents[doc].uuid}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-decoration-none text-success"
                  >
                    Lihat
                  </a>
                ) : (
                  "Tidak ada File"
                )}
              </span>

            </div>
          </div>
        ))}
      </div>

      <button
        className="btn btn-success rounded-pill px-5 mt-5"
        disabled={!allComplete}
      >
        {allComplete ? "Selesai" : "Lengkapi Dulu"}
      </button>
    </section>
  );
}
