"use client";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { FilterSection } from "./FilterSection";

function TableSection() {
  const [students, setStudents] = useState([]);
  const [filteredStudents, setFilteredStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [filters, setFilters] = useState({
    faculty: "semua",
    scholarship: "semua",
    status: "semua"
  });

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        const token = storedUser?.token;

        const response = await axios.get("http://localhost:9900/sms-mgmt/scholarship/get", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const fetchedData = response.data.output_schema.records || [];
        setStudents(fetchedData);
        setFilteredStudents(fetchedData);

      } catch (error) {
        console.error("Failed to fetch student data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchStudents();
  }, []);

  useEffect(() => {
    const lowerSearch = searchTerm.toLowerCase();

    const filtered = students.filter((student) => {
      const nameMatch = `${student.masterUser?.lastName} ${student.masterUser?.firstName}`.toLowerCase().includes(lowerSearch);
      const facultyMatch = filters.faculty === "semua" || student.major?.facultyName === filters.faculty;
      const scholarshipMatch = filters.scholarship === "semua" || student.scholarshipType === filters.scholarship;
      const statusMatch = filters.status === "semua" || student.status === filters.status;

      return nameMatch && facultyMatch && scholarshipMatch && statusMatch;
    });

    setFilteredStudents(filtered);
  }, [searchTerm, filters, students]);

  const renderStatusLegend = (status) => {
    if (status === "DITERIMA") {
      return (
        <img
          src="https://cdn.builder.io/api/v1/image/assets/6e56f22283ca426d8ccf6afbc1731b56/0475a699f051b85d37fc6cfbe257ab03f01e4c0b?placeholderIfAbsent=true"
          alt="Diterima"
          style={{ width: "12px" }}
          className="rounded"
        />
      );
    } else if (status === "BELUM") {
      return (
        <img
          src="https://cdn.builder.io/api/v1/image/assets/6e56f22283ca426d8ccf6afbc1731b56/67229a78890d693d202ed2afe10fec68854874ca?placeholderIfAbsent=true"
          alt="Belum"
          style={{ width: "12px" }}
          className="rounded"
        />
      );
    } else if (status === "DITOLAK") {
      return (
        <img
          src="https://cdn.builder.io/api/v1/image/assets/6e56f22283ca426d8ccf6afbc1731b56/3b2b4bc9d31f7b991cb72b229dffdb4e23526f55?placeholderIfAbsent=true"
          alt="Ditolak"
          style={{ width: "12px" }}
          className="rounded"
        />
      );
    }
    return null;
  };

  if (loading) {
    return <div className="text-center py-5">Loading daftar mahasiswa...</div>;
  }

  return (
    <section className="mb-0 mt-0">
      <FilterSection filters={filters} setFilters={setFilters} />

      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2 className="fs-5 fw-bold">Daftar Pendaftar</h2>
        <input
          type="text"
          className="form-control w-25"
          placeholder="Cari Nama Mahasiswa..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <div className="table-responsive">
        <table className="table table-sm">
          <thead className="bg-light">
            <tr className="small">
              <th className="py-2"></th>
              <th className="py-2">#</th>
              <th className="py-2">Nama Lengkap</th>
              <th className="py-2">NIM</th>
              <th className="py-2">Fakultas</th>
              <th className="py-2">Jurusan</th>
              <th className="py-2">Beasiswa</th>
              <th className="py-2">Tanggal</th>
              <th className="py-2">Status</th>
            </tr>
          </thead>
          <tbody className="small">
            {filteredStudents.length > 0 ? (
              filteredStudents.map((student, index) => (
                <tr key={student.uuid}>
                  <td className="py-1">
                    {renderStatusLegend(student.status)}
                  </td>
                  <td className="py-1">{index + 1}</td>
                  <td className="py-1">
                    {student.masterUser?.lastName || "-"}, {student.masterUser?.firstName || "-"}
                  </td>
                  <td className="py-1">{student.nim || "-"}</td>
                  <td className="py-1">{student.major?.facultyName || "-"}</td>
                  <td className="py-1">{student.major?.majorName || "-"}</td>
                  <td className="py-1">{student.scholarshipType || "-"}</td>
                  <td className="py-1">
                    {student.createdAt
                      ? new Date(student.createdAt).toLocaleDateString('id-ID', {
                          day: '2-digit',
                          month: 'long',
                          year: 'numeric'
                        })
                      : "-"}
                  </td>
                  <td className="py-1">
                    {student.status === "DITERIMA" ? "Diterima" : student.status === "BELUM" ? "Belum" : student.status === "DITOLAK" ? "Ditolak" : "-"}
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="9" className="text-center py-4">
                  Tidak ada mahasiswa ditemukan.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </section>
  );
}

export default TableSection;
