// Handle approval change logic
const handleApprovalChange = async () => {
  if (autoRejected || (!canApprove && approvalStatus === "APPROVED")) {
    alert("Tidak dapat menyetujui karena data/dokumen belum lengkap atau valid.");
    return;
  }

  try {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    const token = storedUser?.token;

    // Update scholarship approval status
    await apiClient.put(`/scholarship/approval?uuid=${uuid}&isApproved=${approvalStatus === 'APPROVED'}`, null, {
      headers: { Authorization: `Bearer ${token}` },
    });

    // Update document completion status
    const isDocumentComplete = documentStatus === "Lengkap"; // Check if documents are complete
    await apiClient.patch(`/document/completion-status?uuid=${uuid}&isComplete=${isDocumentComplete}`, null, {
      headers: { Authorization: `Bearer ${token}` },
    });

    alert("Status pendaftaran berhasil diperbarui.");
    fetchStudentDetails();
  } catch (error) {
    console.error("Error changing approval status:", error);
    alert("Gagal mengubah status pendaftaran.");
  }
};
