"use client";
import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";

export const StudentDetails = () => {
  const { uuid } = useParams(); // Ambil UUID dari URL
  const [student, setStudent] = useState(null);
  const [photoUrl, setPhotoUrl] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStudentDetails = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        const token = storedUser?.token;

        if (!token || !uuid) {
          console.error("Missing token or UUID.");
          return;
        }

        // Fetch scholarship detail
        const response = await axios.get(`http://localhost:9900/sms-mgmt/scholarship/detail?uuid=${uuid}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const studentData = response.data.output_schema.result;
        setStudent(studentData);

        // Fetch document photo
        const docResponse = await axios.get(`http://localhost:9900/sms-mgmt/document/check-completeness?uploadedBy=${studentData?.master_user?.uuid}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const passFoto = docResponse.data.output_schema.records?.find(doc => doc.category === "Pass Foto");
        if (passFoto) {
          setPhotoUrl(`http://localhost:9900/sms-mgmt/document/download?uuid=${passFoto.uuid}`);
        }
      } catch (error) {
        console.error("Error fetching student detail:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchStudentDetails();
  }, [uuid]);

  if (loading) {
    return <div className="text-center py-5">Loading detail mahasiswa...</div>;
  }

  if (!student) {
    return <div className="text-center py-5 text-danger">Data mahasiswa tidak ditemukan.</div>;
  }

  return (
    <section className="mt-0 px-4">
      {/* Navigasi breadcrumb */}
      <div className="d-flex align-items-center gap-2 mb-5">
        <Link to="/bidang/informasi-pendaftar" className="text-decoration-none">
          <img
            src="https://cdn.builder.io/api/v1/image/assets/6e56f22283ca426d8ccf6afbc1731b56/659fa5f3e981f264647a7f21ca78fa1154d428a4?placeholderIfAbsent=true"
            className="d-block"
            style={{ width: "25px", height: "25px", objectFit: "contain" }}
            alt="Navigation"
          />
        </Link>

        <Link to="/bidang/informasi-pendaftar">
          <h2 className="h4 mb-0 fw-bold text-black">Daftar Mahasiswa</h2>
        </Link>
        <span>/</span>
        <h2 className="h4 mb-0 text-primary">Data Pendaftar</h2>
      </div>

      {/* Konten utama */}
      <div className="text-center">
        {/* Foto Profile */}
        {photoUrl ? (
          <img
            src={photoUrl}
            className="rounded-circle mb-4"
            style={{ width: "150px", height: "150px", objectFit: "cover" }}
            alt="Student"
          />
        ) : (
          <div
            className="rounded-circle bg-secondary mb-4"
            style={{ width: "150px", height: "150px" }}
          />
        )}

        {/* Nama Mahasiswa */}
        <h3 className="h4 fw-bold mb-2">
          {student.master_user?.last_name || "-"}, {student.master_user?.first_name || "-"}
        </h3>
        <p className="text-secondary mb-4">
          {student.nim || "-"} / {student.no_registration || "-"}
        </p>

        {/* Informasi Detail */}
        <div className="row">
          <div className="col-md-4">
            <div className="text-start">
              <p className="mb-2">Fakultas :</p>
              <p className="mb-2">Jurusan :</p>
              <p className="mb-2">Email :</p>
              <p className="mb-2">No. Telepon :</p>
              <p className="mb-2">IPK :</p>
              <p className="mb-2">Beasiswa :</p>
            </div>
          </div>
          <div className="col-md-8">
            <div className="text-start">
              <p className="mb-2 fw-medium">{student.major?.faculty_name || "-"}</p>
              <p className="mb-2 fw-medium">{student.major?.major_name || "-"}</p>
              <p className="mb-2 fw-medium">{student.master_user?.email || "-"}</p>
              <p className="mb-2 fw-medium">{student.master_user?.phone_number || "-"}</p>
              <p className="mb-2 fw-medium">{student.master_user?.gpa || "-"}</p>
              <p className="fw-medium">{student.scholarship_type || "-"}</p>
            </div>
          </div>
        </div>

        {/* Aksi Tombol */}
        <div className="d-flex align-items-center gap-3 mt-4">
          <div className="ms-4">
            <Link
              to={`/bidang/informasi-pendaftar/data-pendaftar/validation-and-check/${student.master_user?.uuid}`}
              className="btn btn-light fw-semibold mb-3 px-3"
            >
              Validasi Data dan Dokumen
            </Link>
            <br />
            <button className="btn btn-light fw-semibold px-5">
              Ubah Status
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};
