"use client";
import React, { useEffect, useState } from "react";
import axios from "axios";

function DashboardContent() {
  const [fullName, setFullName] = useState("-");
  const [scholarshipType, setScholarshipType] = useState("-");
  const [registrationDate, setRegistrationDate] = useState("-");

  useEffect(() => {
    const fetchUserAndScholarship = async () => {
      const storedUserRaw = localStorage.getItem("user");
      if (!storedUserRaw) return;

      const storedUser = JSON.parse(storedUserRaw);
      const user = storedUser?.user;
      const token = storedUser?.token;

      if (!user || !token) return;

      const fullNameDisplay = `${user.last_name}, ${user.first_name}`;
      setFullName(fullNameDisplay);

      try {
        const response = await axios.get(
          `http://localhost:9900/sms-mgmt/scholarship/detail?uuid=0a3939b1-bdf5-4201-8f7f-63f6402d23f6`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        
        
        

        const scholarshipRecords = response.data.output_schema.records;

        if (scholarshipRecords?.length > 0) {
          const data = scholarshipRecords[0];
          setScholarshipType(data.scholarshipType || "-");

          if (data.createdAt) {
            const dateObj = new Date(data.createdAt);
            const formattedDate = dateObj.toLocaleDateString("id-ID", {
              day: "2-digit",
              month: "long",
              year: "numeric",
            });
            setRegistrationDate(formattedDate);
          }
        }
      } catch (error) {
        console.error("Gagal mengambil data beasiswa:", error);
      }
    };

    fetchUserAndScholarship();
  }, []);

  return (
    <main className="col-lg-8 mx-auto px-3 py-5">
      <div className="bg-white p-4 rounded-4 shadow-sm mb-4">
        <h2 className="fw-bold text-primary mb-2">Hai, Selamat Datang ðŸ‘‹</h2>
        <p className="h5 text-secondary">{fullName}</p>
      </div>

      <section className="mb-4">
        <div className="bg-light rounded-4 shadow-sm p-4">
          <h4 className="fw-bold mb-3 text-dark">Informasi Beasiswa</h4>
          <div className="card border-0 shadow-sm rounded-4 bg-white p-3">
            <div className="d-flex justify-content-between align-items-center mb-2">
              <span className="text-muted">Jenis Beasiswa</span>
              <span className="fw-semibold text-dark">{scholarshipType}</span>
            </div>
            <hr />
            <div className="d-flex justify-content-between align-items-center">
              <span className="text-muted">Tanggal Daftar</span>
              <span className="fw-semibold text-dark">{registrationDate}</span>
            </div>
          </div>
        </div>
      </section>

      <section>
        <div className="bg-white p-4 rounded-4 shadow-sm">
          <h4 className="fw-bold mb-0 text-dark">ðŸ“¢ Pengumuman</h4>
          <p className="text-muted mt-2">Belum ada pengumuman terbaru saat ini.</p>
        </div>
      </section>
    </main>
  );
}

export default DashboardContent;
