"use client";
import React, { useEffect, useState } from "react";

function DashboardContent() {
  const [fullName, setFullName] = useState("");
  const [scholarshipType, setScholarshipType] = useState("-");

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const user = JSON.parse(storedUser);
      const firstName = user?.user?.first_name || "-";
      const lastName = user?.user?.last_name || "-";
      setFullName(`${lastName}, ${firstName}`);

      const scholarship = user?.user?.scholarship_type || "-";
      setScholarshipType(scholarship);
    }
  }, []);

  return (
    <>
      {/* Import Google Font */}
      <link
        href="https://fonts.googleapis.com/css2?family=Onest:wght@100..900&display=swap"
        rel="stylesheet"
      />
      <main className="col-lg-8" style={{ fontFamily: "'Onest', sans-serif" }}>
        <div className="py-5">
          <h2 className="fw-bold">Hai, Selamat Datang</h2>
          <p className="h4 fw-normal mt-3">{fullName}</p>

          <section className="mt-5">
            <h3 className="fw-bold">Informasi Beasiswa</h3>
            <p className="h5 mt-3">
              Beasiswa: <span className="fw-semibold">{scholarshipType}</span>
            </p>
          </section>

          <section className="mt-5">
            <h3 className="fw-bold">Pengumuman</h3>
          </section>
        </div>
      </main>
    </>
  );
}

export default DashboardContent;
