import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import apiClient from '../../../../../api/apiClient';
import BidangKemahasiswaanLayout from '../../components/BidangKemahasiswaanLayout';
import { Badge } from 'react-bootstrap';

const DetailBeasiswaBidangKemahasiswaan = () => {
  const { uuid } = useParams();
  const navigate = useNavigate();
  const [beasiswa, setBeasiswa] = useState(null);

  useEffect(() => {
    const fetchDetail = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem('user'));
        const token = storedUser?.token;

        const response = await apiClient.get(`/scholarshipType/detail?uuid=${uuid}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        setBeasiswa(response.data.output_schema.result);
      } catch (error) {
        console.error('Gagal mengambil detail beasiswa:', error);
      }
    };

    fetchDetail();
  }, [uuid]);

  if (!beasiswa) return <p className="text-center mt-4">Memuat data...</p>;

  return (
    <BidangKemahasiswaanLayout>
      <div className="container mt-4">
        <h3 className="mb-4">Detail Beasiswa</h3>
        <div className="card shadow-sm">
          <div className="card-body">
            <div className="row">
              <div className="col-md-3 text-center mb-3">
                {beasiswa.logo ? (
                  <img
                    src={beasiswa.logo}
                    alt="Logo Beasiswa"
                    className="img-fluid rounded"
                    style={{ maxHeight: '100px' }}
                  />
                ) : (
                  <div className="text-muted">Tidak ada logo</div>
                )}
              </div>
              <div className="col-md-9">
                <h5>{beasiswa.scholarship_name}</h5>
                <p className="text-muted">{beasiswa.description}</p>
                <p><strong>Kuota:</strong> {beasiswa.applicant_quota}</p>
                <p><strong>IPK Minimum:</strong> {beasiswa.minimum_gpa}</p>
                <p><strong>Semester Minimum:</strong> {beasiswa.minimum_semester}</p>
                <p>
                  <strong>Status:</strong>{' '}
                  <Badge bg={beasiswa.active ? 'success' : 'danger'}>
                    {beasiswa.active ? 'Aktif' : 'Tidak Aktif'}
                  </Badge>
                </p>
                <p><strong>Tanggal Dibuat:</strong> {beasiswa.created_at}</p>
                <p><strong>Terakhir Diubah:</strong> {beasiswa.updated_at}</p>
              </div>
            </div>
          </div>
        </div>
        <button className="btn btn-secondary mt-4" onClick={() => navigate(-1)}>
          Kembali
        </button>
      </div>
    </BidangKemahasiswaanLayout>
  );
};

export default DetailBeasiswaBidangKemahasiswaan;
