import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import apiClient from '../../../../../api/apiClient';
import BidangKemahasiswaanLayout from '../../components/BidangKemahasiswaanLayout';
import { Form, Button } from 'react-bootstrap';

const UpdateBeasiswaBidangKemahasiswaan = () => {
  const { uuid } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    scholarshipName: '',
    description: '',
    applicantQuota: '',
    minimumGpa: '',
    minimumSemester: '',
    logo: '',
    active: true
  });

  useEffect(() => {
    const fetchDetail = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem('user'));
        const token = storedUser?.token;

        const response = await apiClient.get(`/scholarshipType/detail?uuid=${uuid}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        setFormData(response.data.output_schema.result);
      } catch (error) {
        console.error('Gagal memuat detail beasiswa:', error);
      }
    };

    fetchDetail();
  }, [uuid]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const storedUser = JSON.parse(localStorage.getItem('user'));
      const token = storedUser?.token;

      await apiClient.put(`/scholarshipType/update?uuid=${uuid}`, formData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert('Beasiswa berhasil diperbarui!');
      navigate('/bidang/informasi-beasiswa');
    } catch (error) {
      console.error('Gagal mengupdate beasiswa:', error);
    }
  };

  return (
    <BidangKemahasiswaanLayout>
      <div className="container mt-4">
        <h3>Update Beasiswa</h3>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Nama Beasiswa</Form.Label>
            <Form.Control
              type="text"
              name="scholarshipName"
              value={formData.scholarshipName}
              onChange={handleChange}
              required
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Deskripsi</Form.Label>
            <Form.Control
              as="textarea"
              name="description"
              value={formData.description}
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Kuota</Form.Label>
            <Form.Control
              type="number"
              name="applicantQuota"
              value={formData.applicantQuota}
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>IPK Minimum</Form.Label>
            <Form.Control
              type="number"
              step="0.01"
              name="minimumGpa"
              value={formData.minimumGpa}
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Semester Minimum</Form.Label>
            <Form.Control
              type="number"
              name="minimumSemester"
              value={formData.minimumSemester}
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>URL Logo</Form.Label>
            <Form.Control
              type="text"
              name="logo"
              value={formData.logo}
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Check
              type="checkbox"
              label="Aktif"
              name="active"
              checked={formData.active}
              onChange={handleChange}
            />
          </Form.Group>
          <Button type="submit" variant="primary">Simpan Perubahan</Button>
        </Form>
      </div>
    </BidangKemahasiswaanLayout>
  );
};

export default UpdateBeasiswaBidangKemahasiswaan;
