"use client";
import React, { useEffect, useState } from "react";

function Header() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const userData = localStorage.getItem("user");
    if (userData) {
      try {
        setUser(JSON.parse(userData));
      } catch (e) {
        console.error("Failed to parse user data", e);
      }
    }
  }, []);

  const firstName = user?.user?.first_name || user?.first_name || "-";

  return (
    <header className="bg-white shadow-sm py-3">
      <div className="container-fluid px-3 d-flex justify-content-between align-items-center">
        <div className="d-flex align-items-center">
          <img
            src="https://cdn.builder.io/api/v1/image/assets/6e56f22283ca426d8ccf6afbc1731b56/c1a3d0bff214f77f88bc88cdf3abef74f27d80d3?placeholderIfAbsent=true"
            alt="Logo"
            className="img-fluid"
            style={{ width: "35px" }}
          />
          <span className="ms-3 fw-semibold">Sistem Manajemen Beasiswa</span>
        </div>

        <div className="d-flex align-items-center gap-2">
          <span className="fw-normal small">{firstName}</span>
          <img
            src="https://cdn.builder.io/api/v1/image/assets/6e56f22283ca426d8ccf6afbc1731b56/d91c0fffb9e2144c1246549a39cc7bb7f8ce7f4d?placeholderIfAbsent=true"
            alt="User profile"
            className="rounded-circle"
            style={{ width: "35px" }}
          />
        </div>
      </div>
    </header>
  );
}
