"use client";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const documentList = [
  "Pass Foto",
  "Form Biodata A1",
  "Form Keterampilan",
  "Surat Keterangan Tidak Mampu",
  "Resume Pribadi",
  "Motivation Letter",
  "Surat Pernyataan Tidak Menerima Beasiswa Lain",
  "Surat Pernyataan Bermetrai",
  "Fotocopy KTP",
  "Fotocopy KTM",
  "IPK Semester Terakhir",
  "Transkrip Nilai",
];

export default function DocumentValidation() {
  const { uuid } = useParams();
  const [documents, setDocuments] = useState({});
  const [loading, setLoading] = useState(true);
  const [allComplete, setAllComplete] = useState(false);

  useEffect(() => {
    const fetchDocuments = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        const token = storedUser?.token;

        if (!token || !uuid) {
          console.error("Missing token or UUID.");
          return;
        }

        // Fetch student detail
        const detailResponse = await axios.get(`http://localhost:9900/sms-mgmt/scholarship/detail?uuid=${uuid}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        const student = detailResponse.data.output_schema.result;

        // Fetch documents uploaded by student
        const docResponse = await axios.get(`http://localhost:9900/sms-mgmt/document/get?uploadedBy=${uploadedBy}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        

        const uploadedDocuments = docResponse.data.output_schema.records || [];

        // Build documents map
        const newDocuments = {};
        documentList.forEach((docName) => {
          const isUploaded = uploadedDocuments.some((doc) => doc.category === docName);
          newDocuments[docName] = isUploaded;
        });

        setDocuments(newDocuments);

        // Cek semua sudah lengkap
        const isAllComplete = Object.values(newDocuments).every(Boolean);
        setAllComplete(isAllComplete);

      } catch (error) {
        console.error("Error fetching documents:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDocuments();
  }, [uuid]);

  if (loading) {
    return <div className="text-center py-5">Loading cek dokumen...</div>;
  }

  return (
    <section className="mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="fs-4 fw-bold m-0">Cek Dokumen</h2>
        <div className="d-flex gap-2">
          <span>Status</span>
          <span>:</span>
          <span className={allComplete ? "text-success" : "text-danger"}>
            {allComplete ? "Lengkap" : "Tidak Lengkap"}
          </span>
        </div>
      </div>

      <div className="list-group border-0">
        {documentList.map((doc, idx) => (
          <div
            key={idx}
            className="list-group-item border-0 bg-transparent d-flex justify-content-between align-items-center"
          >
            <div className="d-flex gap-3 align-items-center">
              <input
                type="checkbox"
                className="form-check-input"
                checked={documents[doc]}
                readOnly
              />
              <span>{doc}</span>
            </div>
            <div className="d-flex gap-3">
              <span>:</span>
              <span className={documents[doc] ? "text-success" : "text-muted"}>
                {documents[doc] ? "Tersedia" : "Tidak ada File"}
              </span>
            </div>
          </div>
        ))}
      </div>

      <button
        className="btn btn-success rounded-pill px-5 mt-5"
        disabled={!allComplete}
      >
        {allComplete ? "Selesai" : "Lengkapi Dulu"}
      </button>
    </section>
  );
}
