"use client";
import React, { useState } from "react";
import { FaEye, FaEyeSlash, FaKey } from "react-icons/fa";

const ChangePasswordPage = () => {
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmNewPassword, setConfirmNewPassword] = useState("");

  const [showCurrent, setShowCurrent] = useState(false);
  const [showNew, setShowNew] = useState(false);
  const [showConfirm, setShowConfirm] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Password submitted");
  };

  const inputStyle = {
    border: "none",
    backgroundColor: "#f5f7fa",
    padding: "12px 45px 12px 16px",
    borderRadius: "999px",
    width: "100%",
  };

  const eyeIconStyle = {
    position: "absolute",
    top: "50%",
    right: "20px",
    transform: "translateY(-50%)",
    cursor: "pointer",
    color: "#888",
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100" style={{ backgroundColor: "#f2f4f7" }}>
      <div className="bg-white shadow-sm rounded-4 p-4" style={{ width: "100%", maxWidth: "450px" }}>
        <div className="text-center mb-4">
          <FaKey size={42} className="text-dark mb-2" />
          <h4 className="fw-bold text-primary">Change Password</h4>
        </div>
        <form onSubmit={handleSubmit} className="d-flex flex-column gap-4">
          {/* Current Password */}
          <div className="position-relative">
            <label className="form-label text-muted">Current Password</label>
            <input
              type={showCurrent ? "text" : "password"}
              value={currentPassword}
              onChange={(e) => setCurrentPassword(e.target.value)}
              style={inputStyle}
              required
            />
            <span style={eyeIconStyle} onClick={() => setShowCurrent(!showCurrent)}>
              {showCurrent ? <FaEyeSlash /> : <FaEye />}
            </span>
          </div>

          {/* New Password */}
          <div className="position-relative">
            <label className="form-label text-muted">New Password</label>
            <input
              type={showNew ? "text" : "password"}
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              style={inputStyle}
              required
            />
            <span style={eyeIconStyle} onClick={() => setShowNew(!showNew)}>
              {showNew ? <FaEyeSlash /> : <FaEye />}
            </span>
          </div>

          {/* Confirm New Password */}
          <div className="position-relative">
            <label className="form-label text-muted">Confirm New Password</label>
            <input
              type={showConfirm ? "text" : "password"}
              value={confirmNewPassword}
              onChange={(e) => setConfirmNewPassword(e.target.value)}
              style={inputStyle}
              required
            />
            <span style={eyeIconStyle} onClick={() => setShowConfirm(!showConfirm)}>
              {showConfirm ? <FaEyeSlash /> : <FaEye />}
            </span>
          </div>

          <button type="submit" className="btn btn-success rounded-pill fw-semibold mt-3">
            Save
          </button>
        </form>
      </div>
    </div>
  );
};

export default ChangePasswordPage;
