import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import apiClient from '../../../../../api/apiClient';
import BidangKemahasiswaanLayout from '../../components/BidangKemahasiswaanLayout';
import { Form, Button, Card, Row, Col } from 'react-bootstrap';

const UpdateBeasiswaBidangKemahasiswaan = () => {
  const { uuid } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    scholarshipName: '',
    description: '',
    applicantQuota: '',
    minimumGpa: '',
    minimumSemester: '',
    logo: '',
    active: true
  });

  useEffect(() => {
    const fetchDetail = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem('user'));
        const token = storedUser?.token;

        const response = await apiClient.get(`/scholarshipType/detail?uuid=${uuid}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const data = response.data.output_schema?.result;
        if (!data) throw new Error("Data tidak ditemukan");
        setFormData(data);
      } catch (error) {
        console.error('Gagal memuat detail beasiswa:', error);
      }
    };

    fetchDetail();
  }, [uuid]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const storedUser = JSON.parse(localStorage.getItem('user'));
      const token = storedUser?.token;

      await apiClient.put(`/scholarshipType/update?uuid=${uuid}`, formData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert('Beasiswa berhasil diperbarui!');
      navigate('/bidang/informasi-beasiswa');
    } catch (error) {
      console.error('Gagal mengupdate beasiswa:', error);
    }
  };

  return (
    <BidangKemahasiswaanLayout>
      <div className="container mt-4">
        <h3 className="fw-bold mb-4">Update Informasi Beasiswa</h3>

        <Card className="shadow-sm border-0">
          <Card.Body className="p-4">
            <Form onSubmit={handleSubmit}>
              <Row className="mb-3">
                <Col md={6}>
                  <Form.Group>
                    <Form.Label>
                      Nama Beasiswa <span className="text-muted">(Saat ini: {formData.scholarship_name})</span>
                    </Form.Label>
                    <Form.Control
                      type="text"
                      name="scholarshipName"
                      value={formData.scholarshipName}
                      onChange={handleChange}
                      required
                    />
                  </Form.Group>
                </Col>
                <Col md={6}>
                  <Form.Group>
                    <Form.Label>
                      Kuota Pendaftar <span className="text-muted">(Saat ini: {formData.applicant_quota})</span>
                    </Form.Label>
                    <Form.Control
                      type="number"
                      name="applicantQuota"
                      value={formData.applicant_quota}
                      onChange={handleChange}
                    />
                  </Form.Group>
                </Col>
              </Row>

              <Row className="mb-3">
                <Col md={6}>
                  <Form.Group>
                    <Form.Label>
                      IPK Minimum <span className="text-muted">(Saat ini: {formData.minimumGpa})</span>
                    </Form.Label>
                    <Form.Control
                      type="number"
                      step="0.01"
                      name="minimumGpa"
                      value={formData.minimumGpa}
                      onChange={handleChange}
                    />
                  </Form.Group>
                </Col>
                <Col md={6}>
                  <Form.Group>
                    <Form.Label>
                      Semester Minimum <span className="text-muted">(Saat ini: {formData.minimumSemester})</span>
                    </Form.Label>
                    <Form.Control
                      type="number"
                      name="minimumSemester"
                      value={formData.minimumSemester}
                      onChange={handleChange}
                    />
                  </Form.Group>
                </Col>
              </Row>

              <Form.Group className="mb-3">
                <Form.Label>
                  Deskripsi <span className="text-muted">(Saat ini: {formData.description?.slice(0, 30)}...)</span>
                </Form.Label>
                <Form.Control
                  as="textarea"
                  rows={4}
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>
                  URL Logo <span className="text-muted">(Saat ini: {formData.logo ? 'Tersedia' : 'Kosong'})</span>
                </Form.Label>
                <Form.Control
                  type="text"
                  name="logo"
                  value={formData.logo}
                  onChange={handleChange}
                />
              </Form.Group>

              <Form.Group className="mb-4">
                <Form.Check
                  type="checkbox"
                  label="Beasiswa Aktif"
                  name="active"
                  checked={formData.active}
                  onChange={handleChange}
                />
              </Form.Group>

              <div className="d-flex justify-content-end gap-2">
                <Button variant="secondary" onClick={() => navigate(-1)}>
                  Batal
                </Button>
                <Button type="submit" variant="primary">
                  Simpan Perubahan
                </Button>
              </div>
            </Form>
          </Card.Body>
        </Card>
      </div>
    </BidangKemahasiswaanLayout>
  );
};

export default UpdateBeasiswaBidangKemahasiswaan;
