import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";

export const StudentDetails = () => {
  const { uuid } = useParams();
  const [student, setStudent] = useState(null);
  const [photoUrl, setPhotoUrl] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStudentDetails = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        const token = storedUser?.token;

        if (!token || !uuid) {
          console.error("Missing token or UUID.");
          return;
        }

        const response = await axios.get(`http://localhost:9900/sms-mgmt/scholarship/detail?uuid=${uuid}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const studentData = response.data.output_schema.result;
        setStudent(studentData);

        // Fetch profile photo using username
        const docResponse = await axios.get(`http://localhost:9900/sms-mgmt/document/get?uploadedBy=${studentData.master_user.user_name}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const passFoto = docResponse.data.output_schema.records?.find(doc => doc.category === "Pass Foto");
        if (passFoto) {
          setPhotoUrl(`http://localhost:9900/sms-mgmt/document/download?uuid=${passFoto.uuid}`);
        }

      } catch (error) {
        console.error("Error fetching student detail:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchStudentDetails();
  }, [uuid]);

  if (loading) return <div className="text-center py-5">Loading detail mahasiswa...</div>;
  if (!student) return <div className="text-center py-5 text-danger">Data mahasiswa tidak ditemukan.</div>;

  return (
    <section className="mt-0 px-4">
      <div className="d-flex align-items-center gap-2 mb-5">
        <Link to="/bidang/informasi-pendaftar" className="text-decoration-none">
          <img src="https://cdn.builder.io/api/v1/image/assets/6e56f22283ca426d8ccf6afbc1731b56/659fa5f3e981f264647a7f21ca78fa1154d428a4" style={{ width: "25px" }} alt="Back" />
        </Link>
        <Link to="/bidang/informasi-pendaftar">
          <h2 className="h4 mb-0 fw-bold text-black">Daftar Mahasiswa</h2>
        </Link>
        <span>/</span>
        <h2 className="h4 mb-0 text-primary">Data Pendaftar</h2>
      </div>

      <div className="text-center">
        {photoUrl ? (
          <img src={photoUrl} className="rounded-circle mb-4" style={{ width: "150px", height: "150px", objectFit: "cover" }} alt="Student" />
        ) : (
          <div className="rounded-circle bg-secondary mb-4" style={{ width: "150px", height: "150px" }} />
        )}

        <h3 className="h4 fw-bold mb-2">
          {student.master_user.last_name || "-"}, {student.master_user.first_name || "-"}
        </h3>
        <p className="text-secondary mb-4">
          {student.nim || "-"} / {student.no_registration || "-"}
        </p>

        <div className="row">
          <div className="col-md-4 text-start">
            <p>Fakultas :</p>
            <p>Jurusan :</p>
            <p>Email :</p>
            <p>No. Telepon :</p>
            <p>IPK :</p>
          </div>
          <div className="col-md-8 text-start">
            <p className="fw-medium">{student.major?.faculty_name || "-"}</p>
            <p className="fw-medium">{student.major?.major_name || "-"}</p>
            <p className="fw-medium">{student.master_user.email || "-"}</p>
            <p className="fw-medium">{student.master_user.phone_number || "-"}</p>
            <p className="fw-medium">{student.master_user.gpa || "-"}</p>
          </div>
        </div>

        <div className="d-flex align-items-center gap-3 mt-3">
          <span>Beasiswa :</span>
          <span className="fw-medium">{student.scholarship_type || "-"}</span>
        </div>

        <div className="ms-4 mt-4">
          <Link to={`/bidang/informasi-pendaftar/data-pendaftar/validation-and-check/${student.master_user.user_name}`} className="btn btn-light fw-semibold mb-3 px-3">
            Validasi Data dan Dokumen
          </Link>
        </div>
      </div>
    </section>
  );
};
