import React, { useEffect, useState } from "react";
import { RegistrationHeader } from "./RegistrationHeader";
import { FormInput } from "./FormInput";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4 } from "uuid";

function FormPendaftaranGenBI() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    nim: "",
    noRegistration: "",
    scholarshipType: "GenBI",
    addressLine1: "",
    addressLine2: "",
    highSchoolName: "",
    fatherName: "",
    motherName: "",
    majorId: "",
  });

  const [majors, setMajors] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // ✅ Ambil data jurusan dari backend
  useEffect(() => {
    const fetchMajors = async () => {
      const user = JSON.parse(localStorage.getItem("user"));
      const token = user?.token;
  
      if (!token) return;
  
      try {
        setLoading(true);
        
        // Ensure that valid paging parameters are passed
        const page = 8; // Default to first page
        const size = 10; // Default page size
        const sortBy = "name"; // Default sort field
        const orderBy = "asc"; // Default sort order
  
        const res = await axios.get("http://localhost:9900/sms-mgmt/major/get", {
          params: {
            page,
            size,
            sortBy,
            orderBy,
          },
          headers: { Authorization: `Bearer ${token}` },
        });
  
        const fetchedMajors = res.data.output_schema.records || [];
        setMajors(fetchedMajors);
      } catch (error) {
        console.error("Gagal mengambil data jurusan:", error);
      } finally {
        setLoading(false);
      }
    };
  
    fetchMajors();
  }, []);
  
  // ✅ Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  // ✅ Form validation
  const validateForm = () => {
    if (
      !formData.nim ||
      !formData.firstName ||
      !formData.lastName ||
      !formData.majorId
    ) {
      alert("Please fill in all required fields.");
      return false;
    }
    return true;
  };

  // ✅ Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    const requestBody = {
      uuid: uuidv4(),
      nim: formData.nim,
      first_name: formData.firstName,
      last_name: formData.lastName,
      nomor_registrasi: formData.noRegistration,
      scholarship_type: formData.scholarshipType,
      address_line_1: formData.addressLine1,
      address_line_2: formData.addressLine2,
      high_school_name: formData.highSchoolName,
      father_name: formData.fatherName,
      mother_name: formData.motherName,
      major_id: formData.majorId,
    };

    const user = JSON.parse(localStorage.getItem("user"));
    const token = user?.token;

    if (!token) {
      alert("Silakan login terlebih dahulu.");
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:9900/sms-mgmt/scholarship/create",
        requestBody,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      localStorage.setItem("scholarship", JSON.stringify(response.data.output_schema.result));
      alert("Data berhasil dikirim: " + JSON.stringify(response.data));
      navigate("/beasiswa/genbi/form-pendaftaran/document");
    } catch (error) {
      console.error("Gagal mengirim data:", error.response?.data || error.message);
      alert("Gagal mengirim data!");
    }
  };

  // ✅ Render loading state
  if (loading) {
    return <div>Loading majors...</div>;
  }

  return (
    <main
      className="bg-white"
      style={{ fontFamily: "Onest, -apple-system, Roboto, Helvetica, sans-serif" }}
    >
      <div className="container-fluid bg-white py-5 mt-5">
        <div className="row justify-content-center">
          <div className="col-12 col-xxl-10">
            <form className="px-4" onSubmit={handleSubmit}>
              <RegistrationHeader />

              <FormInput
                label="NIM"
                name="nim"
                placeholder="type here"
                value={formData.nim}
                onChange={handleChange}
              />
              <FormInput
                label="Nomor Regis"
                name="noRegistration"
                placeholder="type here"
                value={formData.noRegistration}
                onChange={handleChange}
              />

              <FormInput
                label="Jurusan"
                name="majorId"
                placeholder="Select"
                isSelect
                value={formData.majorId}
                onChange={handleChange}
                options={majors.length > 0 ? majors.map((major) => ({
                  value: major.uuid,
                  label: major.name,
                })) : [{ value: "", label: "No majors available" }]}
              />



              <FormInput
                label="Alamat"
                name="addressLine1"
                placeholder="Type here"
                value={formData.addressLine1}
                onChange={handleChange}
              />
              <FormInput
                name="addressLine2"
                placeholder="Type here"
                value={formData.addressLine2}
                onChange={handleChange}
              />

              <FormInput
                label="Asal Sekolah"
                name="highSchoolName"
                placeholder="Type here"
                value={formData.highSchoolName}
                onChange={handleChange}
              />
              <FormInput
                label="Nama Ayah"
                name="fatherName"
                placeholder="Type here"
                value={formData.fatherName}
                onChange={handleChange}
              />
              <FormInput
                label="Nama Ibu"
                name="motherName"
                placeholder="Type here"
                value={formData.motherName}
                onChange={handleChange}
              />

              <button type="submit" className="btn btn-success px-4 py-2 mt-5 fw-semibold">
                Next
              </button>
            </form>
          </div>
        </div>
      </div>
    </main>
  );
}

export default FormPendaftaranGenBI;
