import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import apiClient from "../../../../api/apiClient";

const AnnouncementDetail = () => {
  const { uuid } = useParams(); // Get the uuid from the URL parameters
  const [announcement, setAnnouncement] = useState(null);

  useEffect(() => {
    if (uuid) {
      const fetchAnnouncementDetail = async () => {
        try {
          const response = await apiClient.get(`/announcement/detail?uuid=${uuid}`);
          setAnnouncement(response.data);
        } catch (error) {
          console.error("Error fetching announcement details:", error);
        }
      };
      fetchAnnouncementDetail();
    } else {
      console.error("No UUID provided");
    }
  }, [uuid]);

  if (!announcement) {
    return <div>Loading...</div>;
  }

  return (
    <section className="py-4">
      <h2 className="h4 fw-bold mb-4">Detail Pengumuman</h2>
      <div>
        <h5>{announcement.title}</h5>
        <p>{announcement.category}</p>
        <p>{new Date(announcement.createdAt).toLocaleDateString()}</p>
        <p>{announcement.description}</p>
      </div>
      <button
        className="btn btn-primary"
        onClick={() => navigate(`/bidang/announcement/update/${uuid}`)}
      >
        Edit Pengumuman
      </button>
    </section>
  );
};

export default AnnouncementDetail;
